import React, { useEffect, useState } from 'react';
import getDataFromApi from '../services/api';
import michaelScottPic from '../img/Michael-Scott.png'

getDataFromApi();
const App = () => {
  const [quote, setQuote] = useState([]);

  useEffect(() => {
    getDataFromApi().then((data) => setQuote(data));
  }, []);

  return (
    <main>
      <div className='container'>
        <img className='image' alt='Michael Scott' src={michaelScottPic} />
        <p className='quote'>{quote}</p></div></main>
  );
};

export default App;
