import React from 'react';
import PropTypes from 'prop-types';
import '../../stylesheets/layout/errorWarning.scss';

const ErrorFilter = (props) => {
  const handleReset = (event) => {
    event.preventDefault();
    props.handleFilterCharacters({
      key: event.currentTarget.id,
    });
  };
  const noName =
    props.filterName === '' ? (
      ''
    ) : (
      <span>
        here called <em>{props.filterName}</em>
      </span>
    );

  const preposition = props.filterSpecies === 'Human' ? 'a ' : 'an ';

  const noSpecies =
    props.filterSpecies === 'All' ? (
      ''
    ) : (
      <span>
        who is {preposition}
        <em>{props.filterSpecies.toLowerCase()}</em>
      </span>
    );

  const noStatus =
    props.filterStatus === 'All' ? (
      ''
    ) : (
      <span>
        and is
        <em>{props.filterStatus.toLowerCase()}</em>
      </span>
    );
  return (
    <section className="section__warning">
      <p>
        <span>There's no one </span> {noName} {noSpecies} {noStatus}
      </p>
      <button id="reset" onClick={handleReset}>
        Try again!
      </button>
      .
    </section>
  );
};

export default ErrorFilter;

ErrorFilter.propTypes = {
  filterName: PropTypes.string,
  handleFilterCharacters: PropTypes.func,
};
