import React, { useEffect, useState } from 'react';
import getDataFromApi from '../services/api';

getDataFromApi();
const App = () => {
  const [quote, setQuote] = useState([]);

  useEffect(() => {
    getDataFromApi().then((data) => setQuote(data));
  }, []);

  return (
    <main className='main'>
      <div className='main__container'>
        <p className='main__container__quote'>{quote}</p>
        <p className='main__container__signature'>Michael Scott</p>
        </div></main>
  );
};

export default App;
